package com.hunter.common.transaction;/** * @Author zhangcl * @Date 17/8/11  下午6:30 */public class TransactionContext {    private static final ThreadLocal<Boolean> context = new ThreadLocal<Boolean>() {        @Override        public Boolean initialValue() {            return Boolean.FALSE;        }    };    private TransactionContext() {    }    public static void enterTransaction() {        context.set(Boolean.TRUE);    }    public static void leaveTransaction() {        context.set(Boolean.FALSE);    }    public static boolean isInTransaction() {        return context.get();    }}